import React from 'react';
import { Link } from 'react-router-dom' 
import {connect} from 'react-redux'; 
import { createStructuredSelector}  from 'reselect'; 

import { auth } from '../../Firebase/firebase.utils';
import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdown from '../cart-dropdown/cart-dropdown.component';
import {selectCartHidden } from '../../redux/cart/carts.selector';
import {selectCurrentUser} from '../../redux/user/user.selector';

import { ReactComponent as Logo } from '../../assests/crown.svg.svg'; 
import {HeaderContainer, LogoContainer, OptionsContainer, OptionLink, OptionsDiv  } from './header.styles';


const Header = ({ currentUser,hidden }) => (
    <HeaderContainer>
        <LogoContainer to ="/">
        <Logo className='logo'/>
        </LogoContainer>
        <OptionsContainer>
            <OptionLink className='option' to ='/shop'>
                 SHOP
            </OptionLink>
            <OptionLink className='option' to ='/shop'>
                 CONTACT
            </OptionLink>
            {currentUser ?
                 <div className='option' onClick={() => auth.signOut()} > SIGN OUT </div>
                :
                <Link className='option' to='/signin'> SIGN IN </Link>
            }
            <CartIcon/>
        </OptionsContainer>{
            hidden ? null :
            <CartDropdown/>
        }
    </HeaderContainer>
) ;

const mapStateToProps = createStructuredSelector ({
    currentUser : selectCurrentUser,
    hidden: selectCartHidden
});



export default  connect(mapStateToProps)(Header);
